// mock API for paintings

const paintings = [
    {
      id: 1,
      name: "Starry Night",
      artist: "Vincent van Gogh",
      price: 250,
      size: "Medium",
      style: "Post-Impressionism",
      image: "https://example.com/starry-night.jpg"
    },
    {
      id: 2,
      name: "The Persistence of Memory",
      artist: "Salvador Dalí",
      price: 300,
      size: "Small",
      style: "Surrealism",
      image: "https://example.com/persistence-memory.jpg"
    }
  ];
  
  export const fetchPaintings = async () => {
    // Simulate API delay
    return new Promise((resolve) => {
      setTimeout(() => {
        resolve(paintings);
      }, 500);
    });
  };
