    id: 4,
    name: "The Scream",
    artist: "Edvard Munch",
    price: 320,
    size: "Medium",
    style: "Expressionism",
    image: "https://upload.wikimedia.org/wikipedia/commons/f/f4/The_Scream.jpg"
  },
  {
    id: 5,
    name: "The Kiss",
    artist: "Gustav Klimt",
    price: 350,
    size: "Large",
    style: "Symbolism",
    image: "https://upload.wikimedia.org/wikipedia/commons/8/8d/Gustav_Klimt_016.jpg"
  },
  {
    id: 6,
    name: "Mona Lisa",
    artist: "Leonardo da Vinci",
    price: 500,
    size: "Medium",
    style: "Renaissance",
    image: "https://upload.wikimedia.org/wikipedia/commons/e/ec/Mona_Lisa,_by_Leonardo_da_Vinci,_from_C2RMF_retouched.jpg"
